import React, { useState } from 'react';
import { TextField, Button, Box, Typography, MenuItem } from '@mui/material';
import axios from 'axios';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    caseType: '',
    location: '',
    language: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/auth/register`, {
        ...formData,
        registrationFee: 499,
      });
      alert('Registration successful! You will be contacted by an advocate soon.');
    } catch (error) {
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <Box sx={{ my: 4, p: 2, bgcolor: 'background.paper', border: '1px solid #D1D5DB', maxWidth: 600, mx: 'auto' }}>
      <Typography variant="h5" gutterBottom>
        Register Your Case (Rs. 499/-)
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Full Name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          sx={{ mb: 2 }}
          required
          variant="outlined"
        />
        <TextField
          fullWidth
          label="Email"
          name="email"
          type="email"
          value={formData.email}
          onChange={handleChange}
          sx={{ mb: 2 }}
          required
          variant="outlined"
        />
        <TextField
          fullWidth
          select
          label="Case Type"
          name="caseType"
          value={formData.caseType}
          onChange={handleChange}
          sx={{ mb: 2 }}
          required
          variant="outlined"
        >
          <MenuItem value="Divorce">Divorce</MenuItem>
          <MenuItem value="Property Dispute">Property Dispute</MenuItem>
          <MenuItem value="Domestic Violence Defense">Domestic Violence Defense</MenuItem>
        </TextField>
        <TextField
          fullWidth
          label="Location"
          name="location"
          value={formData.location}
          onChange={handleChange}
          sx={{ mb: 2 }}
          required
          variant="outlined"
        />
        <TextField
          fullWidth
          select
          label="Preferred Language"
          name="language"
          value={formData.language}
          onChange={handleChange}
          sx={{ mb: 2 }}
          required
          variant="outlined"
        >
          <MenuItem value="English">English</MenuItem>
          <MenuItem value="Hindi">Hindi</MenuItem>
          <MenuItem value="Telugu">Telugu</MenuItem>
          <MenuItem value="Tamil">Tamil</MenuItem>
        </TextField>
        <Button type="submit" variant="contained" fullWidth sx={{ bgcolor: 'accent.main' }}>
          Register Now (Pay Rs. 499/-)
        </Button>
      </form>
    </Box>
  );
}

export default RegistrationForm;
